public class DemoLangBasics1 {
	public static void main(String[] args) {
                                // TODO Auto-generated method stub
		int x=-2;
		if(x==0) {
			System.out.println("zero");
		}
		else if(x%2==0) {
			System.out.println("even");
		}
		else {
			System.out.println("odd");
		}
	}
}
    
